import React, { useState } from 'react';

const basePrice = 5000;

const claddingOptions = {
  None: 0,
  "Thermowood": 0,
  "Cedar": 2880,
  "Fluted Composite": 2400,
  "Painted Timber": 1200,
};

const doorOptions = [
  { name: "Single UPVC", price: 685 },
  { name: "Double UPVC 4ft", price: 1390 },
  { name: "Aluminium Bi-Fold 3m", price: 3370 },
];

const windowOptions = [
  { name: "T1 UPVC", price: 216 },
  { name: "T2 UPVC", price: 245 },
  { name: "T12 LEA UPVC", price: 430 },
];

const extras = [
  { name: "Internal Partition", price: 500 },
  { name: "Internal Door", price: 300 },
  { name: "Canopy", price: 1200 },
  { name: "Decking", price: 900 },
  { name: "Roof Light", price: 600 },
  { name: "Wing/Overhang", price: 950 },
  { name: "Paint Finish", price: 750 },
  { name: "Cedar Oil", price: 350 },
  { name: "Access Charge", price: 200 },
];

export default function App() {
  const [cladding, setCladding] = useState("None");
  const [doors, setDoors] = useState([]);
  const [windows, setWindows] = useState([]);
  const [selectedExtras, setSelectedExtras] = useState([]);

  const addDoor = (door) => setDoors([...doors, door]);
  const addWindow = (window) => setWindows([...windows, window]);
  const toggleExtra = (extra) => {
    setSelectedExtras((prev) =>
      prev.includes(extra) ? prev.filter(e => e !== extra) : [...prev, extra]
    );
  };

  const total =
    basePrice +
    claddingOptions[cladding] +
    doors.reduce((sum, door) => sum + door.price, 0) +
    windows.reduce((sum, win) => sum + win.price, 0) +
    selectedExtras.reduce((sum, ext) => sum + ext.price, 0);

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial' }}>
      <h1>Elite Garden Room Pricing Tool</h1>

      <label>
        <strong>Cladding:</strong>{' '}
        <select value={cladding} onChange={(e) => setCladding(e.target.value)}>
          {Object.keys(claddingOptions).map((type) => (
            <option key={type} value={type}>
              {type} ({claddingOptions[type] > 0 ? `+£${claddingOptions[type]}` : 'Included'})
            </option>
          ))}
        </select>
      </label>

      <div style={{ marginTop: '1rem' }}>
        <strong>Doors:</strong>
        {doorOptions.map((door, i) => (
          <button key={i} onClick={() => addDoor(door)} style={{ margin: '0.3rem' }}>
            Add {door.name} (£{door.price})
          </button>
        ))}
      </div>

      <div style={{ marginTop: '1rem' }}>
        <strong>Windows:</strong>
        {windowOptions.map((win, i) => (
          <button key={i} onClick={() => addWindow(win)} style={{ margin: '0.3rem' }}>
            Add {win.name} (£{win.price})
          </button>
        ))}
      </div>

      <div style={{ marginTop: '1rem' }}>
        <strong>Optional Extras:</strong>
        {extras.map((ext, i) => (
          <label key={i} style={{ display: 'block' }}>
            <input
              type="checkbox"
              checked={selectedExtras.includes(ext)}
              onChange={() => toggleExtra(ext)}
            />
            {' '}
            {ext.name} (£{ext.price})
          </label>
        ))}
      </div>

      <div style={{ marginTop: '2rem', fontSize: '1.4rem' }}>
        <strong>Total: £{total.toLocaleString()}</strong>
      </div>
    </div>
  );
}
